mapper.select:
select 

from tbl_attribute_value
where USERID = #{userid,jdbcType=INTEGER} and ATTRIBUTEID = {attributeid,jdbcType="INTEGER"}

mapper.insert:
insert into tbl_attribute_value
 (ATTRIBUTEID, USERID, VALUE)
values (#{attributeid,jdbcType=INTEGER}, #{userid,jdbcType=INTEGER}, #{value,jdbcType=VARCHAR}

mapper.update:
update tbl_attribute_value
set VALUE = #{value,jdbcType=VARCHAR}
where USERID = #{userid,jdbcType=INTEGER} AND ATTRIBUTEID = #{attributeid,jdbcType=INTEGER}
